import { View, StyleSheet, Text, Image } from 'react-native';
import '../classes/Card';

type CardProps = {
    item: Card;
}

export default function CardListItem(props: CardProps){
    return(
        <View>
        <View style={styles.container}>
          <View style={{width: "80%"}}>
            <Text>Name : {props.item.suit}</Text>
            <Text>{props.item.value} poils</Text>
          </View>
          <View>
            <Image style={styles.teaserImage} source={props.item.image}/>
          </View>
        </View>
        <View style={styles.separator}/>
      </View>
    )
}

const styles = StyleSheet.create({
    container: {
    flex: 1,
    flexDirection: "row",
    marginHorizontal: "5%"
    },
    separator: {
    marginVertical: 4,
    backgroundColor: "rgba(255,255,255,0.3)",
    height: 1,
    width: '90%',
    },
    teaserImage: {
    width: 50,
    height: 50,
    }
});
